Define.Code:
public string[] SplitString(string recipentsString)
{
    string[] recipents = recipentsString.Split(',');
    for (int i=0; i<recipents.Length; i++)
    {
        recipents[i] = recipents[i].Trim();
    }
    return recipents;
}

public int[] ItemIds
{
    get
    {
        string[] idStrings = SplitString((string) Settings["ItemIds"]);
        int[] itemIds = new int[idStrings.Length];
        for (int i = 0; i < idStrings.Length; i++)
        {
            itemIds[i] = Int32.Parse(idStrings[i]);
        }
        return itemIds;
    }
}

public int[] Prices
{
    get
    {
        string[] priceStrings = SplitString((string) Settings["Prices"]);
        int[] prices = new int[priceStrings.Length];
        for (int i = 0; i < priceStrings.Length; i++)
        {
            prices[i] = Int32.Parse(priceStrings[i]);
        }
        return prices;
    }
}

public int StackSize(uint itemId)
{
    return Lua.GetReturnVal<int>("return GetItemInfo("+itemId+")", 7);
}

public void CodHelper(string recipent, int itemId, int price, int amount, bool fullStacks)
{
    Lua.DoString(
        "local id = {0} local stackPrice = {1} local recipent = \"{2}\" local amount = {3} local fullSize = {4} local _,_,_,_,_,_,_,stackSize=GetItemInfo(id) local j=0 local i=0 for b=0,4 do for s=1,GetContainerNumSlots(b) do local n=GetContainerItemLink(b,s) if n then if n:find(id) and i<amount then _,c=GetContainerItemInfo(b,s) if fullSize then if c==stackSize then j=j+c UseContainerItem(b,s) i=i+1 end else j=j+c UseContainerItem(b,s) i=i+1 end end end end end local codprice=j*(stackPrice/stackSize) local header,_=GetSendMailItem(1) SetSendMailCOD(codprice*10000) SendMail(recipent,header,\"\")", itemId, price, recipent, amount, fullStacks);
}

public void Cod(string recipent, int itemId, int price, int amount, bool fullStacks)
{
    int amountLeft = amount;
    int amountToSend;
    while (amountLeft > 0)
    {
        if (amountLeft >= 12)
        {
            amountToSend = 12;
        }
        else
        {
            amountToSend = amountLeft;
        }
        CodHelper(recipent, itemId, price, amountToSend, fullStacks);
        amountLeft -= amountToSend;
        Thread.Sleep(1500);
    }
}

public void SendCod(string[] recipents, int[] itemIds, int[] prices, bool fullStacks)
{
    if (recipents.Length == 0) return;
    for (int i = 0; i < recipents.Length; i++ )
    {
        uint itemId = (uint)itemIds[i];
        int stackSize = StackSize(itemId);
        int amount = (int)Math.Ceiling((double)InbagCount(itemId) / stackSize);
        slog("Mailing {0} stacks of {1} to {2} for {3} per stack.", amount, itemId, recipents[i], prices[i]);
        Cod(recipents[i], (int)itemId, prices[i], amount, fullStacks);
    }
}

private void slog(string format, params object[] args)
{
    Logging.Write(Colors.Crimson, "AHBuddy: " + format, args);
}
